mapper.insert:

 SELECT HISTORY-SEQ.NEXTVAL
     FROM DAUL


INSERT INTO HISTORY_T (
   HISTORY_NO
 , WRITER
 , IP 
) VALUES (
   #{historyNo}
 , #{writer}
 , #{ip} 
)

INSERT INTO File_T (
    FILE_NO
  , UPLOAD_PATH
  , ORIGINAL_FILENAME
  , FILESYSTEM_NAME
  , HISTORY_NO
) VALUES (
    FILE_SEQ.NEXTVAL
  , #{uploadPath}
  , #{originalFileName}
  , #{filesystemName}
  , #{historyNo}
)
