mapper.insert:


  SELECT MEMBER_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO MEMBER_T (
    MEMBER_NO
  , EMAIL
  , NAME
  , GENDER
) VALUES (
    #{memberNo}
  , #{email}
  , #{name}
  , #{gender}
)

INSERT INTO ADDRESS_T (
    ADDRESS_NO
  , ZONECODE
  , ADDRESS
  , DETAIL_ADDRESS
  , EXTRA_ADDRESS
  , MEMBER_NO
) VALUES (
    ADDRESS_SEQ.NEXTVAL
  , #{zonecode}
  , #{address}
  , #{detailAddress}
  , #{extraAddress}
  , #{member.memberNo}
)

mapper.select:
SELECT MEMBER_NO, EMAIL, NAME, GENDER
  FROM MEMBER_T
 WHERE MEMBER_NO = #{memberNo}

SELECT COUNT(*)
  FROM ADDRESS_T
 WHERE MEMBER_NO = #{memberNo}
